import React,{ useState } from 'react' ;

function Table()
{   
    const [field,setField] = useState(
                                        [
                                            {
                                                pos: 55,
                                                player: X,
                                            }
                                        ]
                                     ) ;

    const playerClick = (position) => {
        alert(position) ;
        setField(position) ;
    }

    const playButton = (position) => {
       return( <button className="play-btn" onClick={() => playerClick(position)}> {position} </button>
       )
    }

    return(
        <div className="table-box">
            <p>Position : {field[0].pos}</p>
            <p>Player : {field[0].player}</p>
            <table className="table">
            <tbody>
                <tr>    
                    <td> {playButton(0)} </td>
                    <td> {playButton(1)} </td>
                    <td> {playButton(2)} </td>  
                </tr>
                <tr>
                    <td> {playButton(3)} </td>
                    <td> {playButton(4)} </td>
                    <td> {playButton(5)} </td>
                </tr>
                <tr> 
                    <td> {playButton(6)} </td>
                    <td> {playButton(7)} </td>
                    <td> {playButton(8)} </td>
                </tr>
            </tbody>
            </table>
        </div>
    )
} 



export default Table ;
