        <div className="table-box">
            <table className="table">
                <tr>    
                    <td> <button className="play-btn" onClick={player_Click0}> {field0} </button> </td>
                    <td> <button className="play-btn" onClick={player_Click}> {field1} </button> </td>
                    <td> <button className="play-btn" onClick={player_Click}> </button> </td>
                </tr>
                <tr>
                    <td> <button className="play-btn" onClick={player_Click}> </button> </td>
                    <td> <button className="play-btn" onClick={player_Click}>  </button> </td>
                    <td> <button className="play-btn" onClick={player_Click}> </button> </td>
                </tr>
                <tr> 
                    <td> <button className="play-btn" onClick={player_Click}> </button> </td>
                    <td> <button className="play-btn" onClick={player_Click}> </button> </td>
                    <td> <button className="play-btn" onClick={player_Click}> </button> </td>
                </tr>
            </table>
            <p> Number {value} </p>
            <button onClick={Inc_Number} style={{width:"70px",height:"20px"}}> + </button>
