import React,{ useState } from 'react' ;

function Table()
{
    const [value,setValue] = useState(0) ;
    const [turn,setTurn] = useState(0) ;
    const [field,setField] = useState('') ;

    function Inc_Number()
    {
        setValue(value+1)
    }
    function Dec_Number()
    {
        setValue(value-1)
    }

    function player_Click({position})
    {
        setField0({position}) ;
    }

    return(
        <div className="table-box">
            <table className="table">
                <tr>    
                    <td> <button className="play-btn" onClick={player_Click} position="1"> {field0} </button> </td>
                    <td> <button className="play-btn" onClick={player_Click1} position="2"> {field1} </button> </td>
                    <td> <button className="play-btn" onClick={player_Click2} position="3"> {field2} </button> </td>
                </tr>
                <tr>
                    <td> <button className="play-btn"> </button> </td>
                    <td> <button className="play-btn">  </button> </td>
                    <td> <button className="play-btn"> </button> </td>
                </tr>
                <tr> 
                    <td> <button className="play-btn"> </button> </td>
                    <td> <button className="play-btn"> </button> </td>
                    <td> <button className="play-btn"> </button> </td>
                </tr>
            </table>
            <p> Number {value} </p>
            <button onClick={Inc_Number} style={{width:"70px",height:"20px"}}> + </button>
            <button onClick={Dec_Number} style={{width:"70px",height:"20px"}}> - </button>

        </div>
    )
} 



export default Table ;
