import React,{ useState } from 'react' ;

function Table()
{
    const [value,setValue] = useState(0) ;
    const [turn,setTurn] = useState(0) ;
    const [field0,setField0] = useState('') ;
    const [field1,setField1] = useState('') ;
    const [field2,setField2] = useState('') ;


    function Inc_Number()
    {
        setValue(value+1)
    }
    function Dec_Number()
    {
        setValue(value-1)
    }

    function player_Click0()
    {
        if(turn == 0)
        {
            setField0('X') ;
            setTurn(1) ;
        }
        else if(turn == 1)
        {
            setField0('O') ;
            setTurn(0)
        }
    }

    function player_Click1()
    {
        if(turn == 0)
        {
            setField1('X') ;
            setTurn(1) ;
        }
        else if(turn == 1)
        {
            setField1('O') ;
            setTurn(0)
        }
    }

    function player_Click2()
    {
        if(turn == 0)
        {
            setField2('X') ;
            setTurn(1) ;
        }
        else if(turn == 1)
        {
            setField2('O') ;
            setTurn(0)
        }
    }



    return(
        <div className="table-box">
            <table className="table">
                <tr>    
                    <td> <button className="play-btn" onClick={player_Click0}> {field0} </button> </td>
                    <td> <button className="play-btn" onClick={player_Click1}> {field1} </button> </td>
                    <td> <button className="play-btn" onClick={player_Click2}> </button> </td>
                </tr>
                <tr>
                    <td> <button className="play-btn" onClick={player_Click3}> </button> </td>
                    <td> <button className="play-btn" onClick={player_Click4}>  </button> </td>
                    <td> <button className="play-btn" onClick={player_Click5}> </button> </td>
                </tr>
                <tr> 
                    <td> <button className="play-btn" onClick={player_Click6}> </button> </td>
                    <td> <button className="play-btn" onClick={player_Click7}> </button> </td>
                    <td> <button className="play-btn" onClick={player_Click8}> </button> </td>
                </tr>
            </table>
            <p> Number {value} </p>
            <button onClick={Inc_Number} style={{width:"70px",height:"20px"}}> + </button>
            <button onClick={Dec_Number} style={{width:"70px",height:"20px"}}> - </button>

        </div>
    )
} 



export default Table ;
