import React,{ useState } from 'react' ;

function Table()
{   
    const [history,setHistory] = useState([]) ;
    const [field,setField] = useState(Array(9).fill(null)) ;
    const [turn,setTurn] = useState(0) ;
    let win_condition = [
                            [0,1,2],
                            [3,4,5],
                            [6,7,8]
                        ]

    // debug array
    console.log(history) ;
    console.log(field) ;

    const playerClick = (position) => {
        // alert(position) ; Check position value
        // setState([...previousArray,newValue]) ; setState with array

        if(field[position] == null)
        {
            if(turn == 0) // X player turn
            {
                field[position] = 'X' ;
                setField([...field]) ;
                setHistory([...history, {pos : position, player:'X'}]) ; // Set State History
                winner() ;
                setTurn(1) ;
            }
            else if(turn == 1) // O player turn
            {
                field[position] = 'O'
                setField([...field]) ;
                setHistory([...history, {pos : position, player: 'Y'}]) ; // Set State History
                winner() ;
                setTurn(0) ;
            }
        }    
    }

    const winner = () => {
        for(let i=0 ; i<=win_condition.length-1 ; i++)
        {
            let [x,y,z] = win_condition[i] ;

            if(field[x] === field[y]  &&  field[x] === field[z] && field[y] === field[z] && field[x],field[y],field[z] != null)
            {
                turn == 0 ? alert('WINNER : X') : alert ('WINNER : O') ;
                console.log(x,y,z) ;
            }
        }
    }

    const playButton = (position) => {
       return( <button className="play-btn" onClick={() => playerClick(position)}> <p>{field[position]}</p> </button>
       )
    }

    return(
        <div className="table-box">
            <table className="table">
            <tbody>
                <tr>    
                    <td> {playButton(0)} </td>
                    <td> {playButton(1)} </td>
                    <td> {playButton(2)} </td>  
                </tr>
                <tr>
                    <td> {playButton(3)} </td>
                    <td> {playButton(4)} </td>
                    <td> {playButton(5)} </td>
                </tr>
                <tr> 
                    <td> {playButton(6)} </td>
                    <td> {playButton(7)} </td>
                    <td> {playButton(8)} </td>
                </tr>
            </tbody>
            </table>
        </div>
    )
} 



export default Table ;
