import React,{ useState } from 'react' ;

function Table()
{   
    const [field,setField] = useState([]) ;
    const [player,setPlayer] = useState(Array(9).fill(null)) ;
    console.log(field) ;
    console.log(player) ;

    const playerClick = (position) => {
        alert(position) ;
        // setField([...previousArray,newValue]) ; setState with array
        // setField([...field, {pos : position, player:'X'}]) ; 
        setPlayer([...player.slice(1)]) ;

    }

    const playButton = (position,field) => {
       return( <button className="play-btn" onClick={() => playerClick(position)}>  </button>
       )
    }

    return(
        <div className="table-box">
            <p>Position :</p>
            <p>Player : </p>
            <table className="table">
            <tbody>
                <tr>    
                    <td> {playButton(0)} </td>
                    <td> {playButton(1)} </td>
                    <td> {playButton(2)} </td>  
                </tr>
                <tr>
                    <td> {playButton(3)} </td>
                    <td> {playButton(4)} </td>
                    <td> {playButton(5)} </td>
                </tr>
                <tr> 
                    <td> {playButton(6)} </td>
                    <td> {playButton(7)} </td>
                    <td> {playButton(8)} </td>
                </tr>
            </tbody>
            </table>
        </div>
    )
} 



export default Table ;
