const playerClick = (position) => {
    const field = Array(9).fill(null) ;
    alert(position) 
    field[position] = position ;

    for(var x = 0 ; x<=8 ; x++)
    {
        console.log(field[x])
    }
}




function PlayButton({position})
{   
    return(
        <button className="play-btn" onClick={() => playerClick(position)}> {position} </button>
    )
}

export default PlayButton ;
