import React, { useState } from 'react' ;

const PlayerClick = (position) => {
    const [field,setField] = useState() ;
    setField(field = position)
    alert(position) ;   
}


function PlayButton({position},{field})
{   
    return(
        <button className="play-btn" onClick={() => PlayerClick(position)} > {position}{field} </button>
    )
}

export default PlayButton ;
